        int x,y;
    public:
        /**
         * @brief 
         * @param a x
         * @param b y
        ***/
        void set(int a,int b){
           x = a;
           y = b;
        }
        /**
         * @brief 
         * @param n 
         * @return 
        ***/
        float dist(node n){
           return (this->x - n.x)*(this->x - n.x) + (this->y - n.y)*(this->y - n.y);
        }
};
